public class AdvertOfCode2015zad1 {
    public static void main(String[] args) {

        String pietra;
        Scanner in = new Scanner(System.in);
        System.out.println("wpisz pietra (nawiasy): ");
        pietra = in.nextLine();

        int doGory = 0;
        int doDolu = 0;
        for (int i = 0; i < pietra.length(); i++) {
            if (pietra.charAt(i) == '(')
                doGory++;
            if (pietra.charAt(i) == ')')
                doDolu++;
        }
        System.out.println("Mikolaj poruszal sie do gory " + doGory + "razy");
        System.out.println("Mikolaj poruszal sie do dolu " + doDolu + "razy");
        System.out.println("Mikolaj dotarl na " + (doGory - doDolu) + " pietro");
    }
}
